section.programlisting:
1 #ifndef __KEYBOARD_H__
2 #define __KEYBOARD_H__
3 
4 #include <sys/ksys.h>
5 
6 namespace KolibriLib
7 {
8     namespace keyboard
9     {
12         char CheckKeyboard()
13         {
14             return _ksys_get_key().code;
15         }
16 
17         enum ControlKeys
18         {
19             LeftShift   = KSYS_CONTROL_LSHIFT,  //Хоспади автор ksys.h зачем такие страшные и длинные шифры, нипанятна же ничего
20             RightShift  = KSYS_CONTROL_RSHIFT,
21             LeftCtrl    = KSYS_CONTROL_LCTRL,
22             RightCtrl   = KSYS_CONTROL_RCTRL,
23             LeftAlt     = KSYS_CONTROL_LALT,
24             RightAlt    = KSYS_CONTROL_RALT,
25             CapsLock    = KSYS_CONTROL_CAPS,
26             NumLock     = KSYS_CONTROL_NUM_LOCK,
27             ScrollLock  = KSYS_CONTROL_SCROLL_LOCK
28         };
29 
32         unsigned GetControlKey()
33         {
34             return _ksys_get_control_key_state();
35         }
36 
37 
41         bool InstallHotKey(uint16_t controlkeys)
42         {
43             return !_ksys_set_sys_hotkey(0, controlkeys);
44         }
45     } // namespace keyboard
46 } // namespace KolibriLib
47 
48 
49 #endif // __KEYBOARD_H__
