29             inline unsigned int GetTextSize()
30             {
31                 unsigned Size;
32                 asm_inline(
33                     "int $0x40"
34                     : "=c"(Size)
35                     : "a"(48), "b"(11)
36                 );
37                 return Size;
38             }
39 
43             inline void SetTextSize(unsigned newSize)
44             {
45                 asm_inline(
46                     "int $0x40"
47                     ::"a"(48), "b"(12), "c"(newSize)
48                 );
49             }
50 
56             inline void DrawText(const std::string &text, const Coord &coord, const unsigned &size = 9, const Colors::Color &color = OS::sys_color_table.work_text)
57             {
58                 SetTextSize(size);
59                 _ksys_draw_text(text.c_str(), coord.x, coord.y, text.length(), color.val);
60             }
