73             void DrawText(const std::string &text,
74                           const Coord &coord, const Size &size = Fonts::DefaultFont.size,
75                           unsigned flags = Flags::Normal,
76                           unsigned margin = UI::DefaultMargin,
77                           const Colors::Color &colorText = OS::sys_color_table.work_text,
78                           const Colors::Color &BackgroundColor = OS::sys_color_table.work_area)
79             {
80                 const unsigned w = ((margin * 2) + size.x);
81                 const unsigned h = ((margin * 2) + (size.y * text.length()));
82 
83                 rgb_t *canvas = new rgb_t[w * h];
84 
85                 if(BackgroundColor._a < 1)
86                 {                           //прозрачность фона
87                     const float k = BackgroundColor._a / 255;
88                     for (int i = 0; i < h; i++)
89                     {
90                         for (int j = 0; j < w; i++)
91                         {
92                             canvas[i * h + j] = Colors::BlendColors(graphic::ReadPoint(window::GetWindowCoord() + coord + Coord(0, i) + Coord(j, 0)),
93                                                                     BackgroundColor,
94                                                                     k)
95                                                     .GetRGB();
96                         }
97                     }
98                 }
99                 else
100                 {
101                     memset((void*)canvas, Colors::RGBtoINT(BackgroundColor.GetRGB()), w * h);
102                 }
103 
104 
105                 drawText(canvas, coord.x, coord.y, text.c_str(), text.length(), colorText.val, flags);
106 
107                 if(colorText._a < 1)
108                 {                       //Прозрачность текста
109                     const float k = colorText._a / 255;
110                     for (int i = margin; i < h - margin; i++)   //Трогать поля нет смысла
111                     {
112                         for (int j = margin; j < w - margin; i++)
113                         {
114                             Colors::Color b = graphic::ReadPoint(window::GetWindowCoord() + coord + Coord(0, i) + Coord(j, 0));
115                             if (b != canvas[i * (h - margin) + j])
116                             {
117                                 canvas[i * (h - margin) + j] = Colors::BlendColors(b,
118                                                                                canvas[i * (h - margin) + j],
119                                                                                k)
120                                                                 .GetRGB();
121                             }
122                             
123                         }
124                     }
125                 }
126 
127                 _ksys_draw_bitmap(canvas, coord.x, coord.y, w, h);
128 
129                 delete[] canvas;
130             }
131 
140             void DrawText(const std::string &text,
141                           const Coord &coord, const Fonts::Font &font = Fonts::DefaultFont,
142                           unsigned margin = UI::DefaultMargin,
143                           const Colors::Color &colorText = OS::sys_color_table.work_text,
144                           const Colors::Color &BackgroundColor = OS::sys_color_table.work_area)
145             {
146                 const unsigned w = ((margin * 2) + font.size.x);
147                 const unsigned h = ((margin * 2) + (font.size.y * text.length()));
148 
149                 rgb_t *canvas = new rgb_t[w * h];
150 
151                 if (BackgroundColor._a < 1)
152                 { // прозрачность фона
153                     const float k = BackgroundColor._a / 255;
154                     for (int i = 0; i < h; i++)
155                     {
156                         for (int j = 0; j < w; i++)
157                         {
158                             canvas[i * h + j] = Colors::BlendColors(graphic::ReadPoint(window::GetWindowCoord() + coord + Coord(0, i) + Coord(j, 0)),
159                                                                     BackgroundColor,
160                                                                     k)
161                                                     .GetRGB();
162                         }
163                     }
164                 }
165                 else
166                 {
167                     memset((void *)canvas, Colors::RGBtoINT(BackgroundColor.GetRGB()), w*h);
168                 }
169 
170                 drawText(canvas, coord.x, coord.y, text.c_str(), text.length(), colorText.val, font._Flags);
171 
172                 if (colorText._a < 1)
173                 { // Прозрачность текста
174                     const float k = colorText._a / 255;
175                     for (int i = margin; i < h - margin; i++) // Трогать поля нет смысла
176                     {
177                         for (int j = margin; j < w - margin; i++)
178                         {
179                             Colors::Color b = graphic::ReadPoint(window::GetWindowCoord() + coord + Coord(0, i) + Coord(j, 0));
180                             if (b != canvas[i * (h - margin) + j])
181                             {
182                                 canvas[i * (h - margin) + j] = Colors::BlendColors(b,
183                                                                                    canvas[i * (h - margin) + j],
184                                                                                    k)
185                                                                    .GetRGB();
186                             }
187                         }
188                     }
189                 }
190 
191                 _ksys_draw_bitmap(canvas, coord.x, coord.y, w, h);
192 
193                 delete[] canvas;
194             }
