264         template <class T>
265         int KolibriLib::window::Window::AddElement(const T &element)
266         {
267             Element a;
268 
269             a.SetElement(element);
270 
271             for (int i = 0; i < _Elements.size(); i++)
272             {
273                 if (_Elements.count(i) == 0)
274                 {
275                     _Elements[i] = a;
276                     return i;
277                 }
278             }
279         }
