281         template <class T>
282         void KolibriLib::window::Window::SetElement(int i, const T &element)
283         {
284             if (_Elements.count(i))
285             {
286                 _Elements[i].SetElement(element);
287                 return;
288             }
289             _ksys_debug_puts("KolibriLib::window::Window::SetElement: i >= _Elements.size(), return\n");
290         }
291 
292         template <class T>
293         const T &KolibriLib::window::Window::GetElement(int i) const
294         {
295             auto it = _Elements.find(i);
296             if (it != _Elements.end())
297             {
298                 switch (it->second._type)
299                 {
300                 case Element::Type::TextLabel:
301                     return it->second.txt;
302                 case Element::Type::Button:
303                     return it->second.btn;
304                 case Element::Type::Image:
305                     return it->second.img;
306                 case Element::Type::Form:
307                     return it->second.form;
308                 case Element::Type::CheckBox:
309                     return it->second.checkbox;
310                 case Element::Type::Frame:
311                     return it->second.frame;
312                 case Element::Type::Menu:
313                     return it->second.menu;
314                 default:
315                     _ksys_debug_puts("KolibriLib::window::Window::GetElement: unknown type, break\n");
316                     break;
317                 }
318             }
319             _ksys_debug_puts("KolibriLib::window::Window::SetElement: i >= _Elements.size(), return\n");
320 
321             return 0;
322         }
