337         void KolibriLib::window::Window::Element::free()
338         {
339             switch (_type)
340             {
341             case Type::Button:
342                 delete btn;
343                 break;
344             case Type::Image:
345                 delete img;
346                 break;
347             case Type::CheckBox:
348                 delete checkbox;
349                 break;
350             case Type::Form:
351                 delete form;
352                 break;
353             case Type::TextLabel:
354                 delete txt;
355                 break;
356             case Type::Menu:
357                 delete menu;
358                 break;
359             case Type::Frame:
360                 delete frame;
361                 break;
362             default:
363                 break;
364             }
365         }
