369         KolibriLib::window::Window::Window(const std::string &Title, const KolibriLib::Size &size, const KolibriLib::Colors::ColorsTable &colors, const KolibriLib::Colors::Color &TitleColor, bool Resize, bool RealtimeRedraw, bool Gradient, unsigned Transparency, const unsigned &Margin)
370         {
371             #if DEBUG == true
372             _ksys_debug_puts("KolibriLib::window:::Window constructor\n");
373             #endif
374 
375             _title = Title;
376             _size = size;
377             _MARGIN = Margin;
378             _TitleColor = TitleColor;
379             _Transparency = Transparency;
380             _RealtimeRedraw = RealtimeRedraw;
381 
382             /*DCBAYYYY*/
383             _style = 0b00100000;
384             if (Resize)
385             {
386                 _style += WindowStyle::CanResize;
387             }
388             else
389             {
390                 _style += WindowStyle::FixSize;
391             }
392 
393             if (Gradient)
394             {
395                 _style += WindowStyle::GradientDraw;
396             }
397             else
398             {
399                 _style += WindowStyle::NormalDraw;
400             }
401 
402             if (colors == Colors::DefaultColorTable) // Если небыла в аргументах таблица цветов
403             {                                        // Используется системная
404                 _colors = OS::GetSystemColors();
405             }
406             else
407             {
408                 _colors = colors;
409             }
410 
411             if (_Transparency > 0)
412             {
413                 _style += WindowStyle::NoDrawWorkspace;
414             }
415 
416             window::CreateWindow(DefaultWindowCoord, _size, _title, _colors.frame_area, _TitleColor, _style); // Отрисовать окно
417         }
