423         void KolibriLib::window::Window::Redraw()
424         {
425             StartRedraw();
426             ChangeWindow(GetCoord(), _size);
427 
428             if (_Transparency > 0 && false)
429             {
430                 /*UI::Images::img buff;
431                 for (unsigned i = 0; i < GetWindowSize().y; i++)
432                 {
433                     for (unsigned j = 0; j < GetWindowSize().x; j++)
434                     {
435                         buff.SetPixel(Colors::BlendColors(Background::ReadPoint(Coord(j, i)), , _Transparency / 100))
436 
437                     }
438                 }*/
439             }
440             else
441             {
442                 KolibriLib::graphic::DrawRectangleFill({0, (int)window::GetSkinHeight()}, GetWindowSize(), _colors.frame_area);
443             }
444 
445             for (const std::pair<int, Window::Element> &n : Window::_Elements)
446             {
447                 switch (n.second._type)
448                 {
449                 case KolibriLib::window::Window::Element::Type::TextLabel:
450                     n.second.txt->Render();
451                     break;
452                 case KolibriLib::window::Window::Element::Type::Button:
453                     n.second.btn->Render();
454                     break;
455                 case KolibriLib::window::Window::Element::Type::Image:
456                     n.second.img->Render();
457                     break;
458                 case KolibriLib::window::Window::Element::Type::Form:
459                     n.second.form->Render();
460                     break;
461                 case KolibriLib::window::Window::Element::Type::CheckBox:
462                     n.second.checkbox->Render();
463                     break;
464                 case KolibriLib::window::Window::Element::Type::Frame:
465                     n.second.frame->Render();
466                     break;
467                 case KolibriLib::window::Window::Element::Type::Menu:
468                     n.second.menu->Render();
469                     break;
470                 default:
471                     _ksys_debug_puts("KolibriLib::window::Window::Render unknown type, break\n");
472                     break;
473                 }
474             }
475 
476             if (_Transparency != 0) // Прозрачность окна
477             {
478                 for (int i = 0; i < _size.y; i++)
479                 {
480                     for (int j = 0; j < _size.x; j++)
481                     {
482                         graphic::DrawPixel({j, i}, Colors::BlendColors(graphic::ReadPoint({j, i}), Background::ReadPoint({j, i}), 100 / _Transparency)); // Пока так, потом может быть станет лучше
483                     }
484                 }
485             }
486 
487             EndRedraw();
488         }
