531         void Window::Render(const Coord &coord)
532         {
533             StartRedraw();
534             window::CreateWindow(coord, _size, _title, _colors.frame_area, _TitleColor, _style);
535 
536             for (const std::pair<int, Element> &n : _Elements)
537             {
538                 switch (n.second._type)
539                 {
540                 case Element::Type::TextLabel:
541                     n.second.txt->Render();
542                     break;
543                 case Element::Type::Button:
544                     n.second.btn->Render();
545                     break;
546                 case Element::Type::Image:
547                     n.second.img->Render();
548                     break;
549                 case Element::Type::Form:
550                     n.second.form->Render();
551                     break;
552                 case Element::Type::CheckBox:
553                     n.second.checkbox->Render();
554                     break;
555                 case Element::Type::Menu:
556                     n.second.menu->Render();
557                     break;
558                 case Element::Type::Frame:
559                     n.second.frame->Render();
560                     break;
561                 default:
562                     break;
563                 }
564             }
565 
566             if (_Transparency != 0) // Прозрачность окна
567             {
568                 Size size = GetWindowSize();
569                 for (int i = 0; i < size.y; i++)
570                 {
571                     for (int j = 0; j < size.x; j++)
572                     {
573                         graphic::DrawPixel({j, i}, Colors::BlendColors(graphic::ReadPoint({j, i}), Background::ReadPoint({j, i}), 100 / _Transparency));
574                     }
575                 }
576             }
577 
578             EndRedraw();
579         }
