603         OS::Event Window::Handler()
604         {
605             OS::Event event = OS::WaitEvent(8);
606 
607             switch (event)
608             {
609             case OS::Events::Redraw:
610 
611                 Render();
612 
613                 break;
614             case OS::Events::Button:
615 
616                 if (UI::buttons::GetPressedButton() == 1) // Если нажата кнопка X
617                 {
618                     return OS::Events::Exit;
619                 }
620 
621                 for (const std::pair<int, Element> &n : _Elements) // Запуск обработчиков всех используемых элементов
622                 {
623                     switch (n.second._type)
624                     {
625                     case Element::Type::Form:
626                         n.second.form->ButtonHandler();
627                     case Element::CheckBox:
628                         n.second.checkbox->Handler();
629                     case Element::Button:
630                         n.second.btn->Handler();
631                     }
632                 }
633 
634                 break;
635             case OS::Events::Key:
636 
637                 if (activeForm != -1)
638                 {
639                     _Elements[activeForm].form->Handler(); // Обработчик активной на текущий момент формы
640                 }
641 
642                 break;
643             }
644             if (_RealtimeRedraw)
645             {
646                 Coord Mouse = mouse::GetMousePositionInWindow();
647                 if (((Mouse.x > 0 && Mouse.y > 0) && Mouse.x < GetWindowSize().x && Mouse.y < GetSkinHeight()) && mouse::GetMouseButtons() == mouse::LeftButton)
648                 {
649                     while (mouse::GetMouseButtons() == mouse::LeftButton)
650                     {
651                         Render(mouse::GetMousePositionOnSreen() - Mouse);
652                         _ksys_thread_yield();
653                     }
654                 }
655             }
656 
657             return event;
658         }
659 
660         UI::buttons::ButtonID Window::GetPressedButton()
661         {
662             for (const std::pair<int, Element> &n : _Elements)
663             {
664                 if (n.second._type == Element::Type::Button)
665                 {
666                     if (n.second.btn->GetStatus())
667                     {
668                         return n.second.btn->GetId();
669                     }
670                 }
671             }
672         }
673         const std::string &Window::GetInputFromFrom(int form) const
674         {
675             auto it = _Elements.find(form);
676             if (it->second._type == Window::Element::Type::Form)
677             {
678                 return it->second.form->GetInput();
679             }
