232         unsigned Window::AddNewButton(UI::buttons::Button btn)
233         {
234             for(unsigned i = 0; i < _Elements.size(); i++)
235             {
236                 if (!_Elements[i].use)
237                 {
238                     switch (btn.GetType())
239                     {
240                     case UI::buttons::Button::Type::Image:
241                         _Elements[i].btn = btn;
242                         break;
243                     case UI::buttons::Button::Type::Text:
244                         _Elements[i].btn.init(btn.GetCoord(), btn.GetSize(), btn.GetTextLabel(), btn.GetMargin(), btn.GetColor());
245                         break;
246                     default:
247                         break;
248                     }
249                     _Elements[i]._type = Element::Type::Button;
250                     _Elements[i].use = true;
251                     return i;
252                 }
253             }
254             Element a;
255             switch (btn.GetType())
256             {
257             case UI::buttons::Button::Type::Image:
258                 a.btn.init(btn.GetCoord(), btn.GetSize(), btn.GetImage(), btn.GetMargin(), btn.GetColor());
259                 break;
260             case UI::buttons::Button::Type::Text:
261                 a.btn.init(btn.GetCoord(), btn.GetSize(), btn.GetTextLabel(), btn.GetMargin(), btn.GetColor());
262             default:
263                 break;
264             }
265             a.use = true;
266             _Elements.push_back(a);
267             return _Elements.size();
268         }
