309         unsigned Window::AddNewForm(UI::Form form)
310         {
311             for (unsigned i = 0; i < _Elements.size(); i++)
312             {
313                 if (!_Elements[i].use)
314                 {
315                     _Elements[i].frm.init(form.GetCoord(), form.GetSize(), NULL, form.GetColor(), form.GetBackgroundColor(), form.GetMargin());
316                     _Elements[i]._type = Element::Type::Form;
317                     _Elements[i].use = true;
318                     return i;
319                 }
320             }
321             Element a;
322             a.frm.init(form.GetCoord(), form.GetSize(), NULL, form.GetColor(), form.GetBackgroundColor(), form.GetMargin());
323             a.use = true;
324             a._type = Element::Type::Form;
325             _Elements.push_back(a);
326             return _Elements.size();
327         }
