329         Window::Window(std::string Title, UI::Size size, int style, Color::ColorsTable colors, unsigned Margin)
330         {
331             _ksys_debug_puts("window:");
332             _title = Title;
333             _size = size;
334             _style = style;
335             _MARGIN = Margin;
336 
337             if (Color::ComparisonColorsTables(colors, Color::DefaultColorTable))    //Если небыла в аргументах таблица цветов
338             {                                                                       //Используется системная
339                 _colors = OS::GetSystemColors();
340             }
341             else
342             {
343                 _colors = colors;
344             }
345             
346             DrawWindow(DefaultWindowCoord);                                         //Отрисовать окно
347         }
