394         void Window::Render()
395         {
396             StartRedraw();
397             DrawWindow();
398 
399             for (unsigned i = 0; i < _Elements.size(); i++)
400             {
401                 if (_Elements[i].use)
402                 {
403                     switch (_Elements[i]._type)
404                     {
405                     case Element::Type::Button:
406                         _Elements[i].btn.Render();
407                         break;
408                     case Element::Type::TextLabel:
409                         _Elements[i].txt.Render();
410                         break;
411                     case Element::Type::CheckBox:
412                         _Elements[i].ChckBx.Render();
413                         break;
414                     case Element::Type::Image:
415                         _Elements[i].img.Render();
416                         break;
417                     case Element::Type::Form:
418                         _Elements[i].frm.Render();
419                         break;
420                     default:
421                         _ksys_debug_puts("Error in KolibriLib::window::Window::Render(): unklown type\n");
422                         break;
423                     }
424                 }
425             }
426 
427             EndRedraw();
428         }
