333         Window::~Window()
334         {
335             for(unsigned i = 0; i < _Elements.size(); i++)
336             {
337                 _Elements[i].use = false;
338                 _Elements[i].btn.~Button();
339                 _Elements[i].ChckBx.~CheckBox();
340                 _Elements[i].txt.~TextLabel();
341                 _Elements[i].frm.~Form();
342                 _Elements[i].img.~image();
343             }
344         }
