264         template <class T>
265         int KolibriLib::window::Window::AddElement(const T element)
266         {
267             #ifdef DEBUG
268             _ksys_debug_puts("Add element-\n");
269             #endif
270 
271             Element a;
272 
273             a.SetElement(element);
274 
275             for (int i = 0; i < _Elements.size(); i++)
276             {
277                 if (_Elements.count(i) == 0)
278                 {
279                     _Elements[i] = a;
280                     return i;
281                 }
282             }
283         }
