285         template <class T>
286         void KolibriLib::window::Window::SetElement(int i, const T &element)
287         {
288             if (_Elements.count(i))
289             {
290                 _Elements[i].SetElement(element);
291                 return;
292             }
293             _ksys_debug_puts("KolibriLib::window::Window::SetElement: i >= _Elements.size(), return\n");
294             return;
295         }
296 
297         template <class T>
298         const T &KolibriLib::window::Window::GetElement(int i) const
299         {
300             auto it = _Elements.find(i);
301             if (it != _Elements.end())
302             {
303                 switch (it->second._type)
304                 {
305                 case Element::Type::TextLabel:
306                     return it->second.txt;
307                 case Element::Type::Button:
308                     return it->second.btn;
309                 case Element::Type::Image:
310                     return it->second.img;
311                 case Element::Type::Form:
312                     return it->second.form;
313                 case Element::Type::CheckBox:
314                     return it->second.checkbox;
315                 case Element::Type::Frame:
316                     return it->second.frame;
317                 case Element::Type::Menu:
318                     return it->second.menu;
319                 default:
320                     _ksys_debug_puts("KolibriLib::window::Window::GetElement: unknown type, break\n");
321                     break;
322                 }
323             }
324             _ksys_debug_puts("KolibriLib::window::Window::SetElement: i >= _Elements.size(), return\n");
325 
326             return 0;
327         }
