342         void KolibriLib::window::Window::Element::free()
343         {
344             switch (_type)
345             {
346             case Type::Button:
347                 delete btn;
348                 break;
349             case Type::Image:
350                 delete img;
351                 break;
352             case Type::CheckBox:
353                 delete checkbox;
354                 break;
355             case Type::Form:
356                 delete form;
357                 break;
358             case Type::TextLabel:
359                 delete txt;
360                 break;
361             case Type::Menu:
362                 delete menu;
363                 break;
364             case Type::Frame:
365                 delete frame;
366                 break;
367             default:
368                 break;
369             }
370         }
