374         KolibriLib::window::Window::Window(const std::string &Title, const KolibriLib::Size &size, const KolibriLib::Colors::ColorsTable &colors, const KolibriLib::Colors::Color &TitleColor, bool Resize, bool RealtimeRedraw, bool Gradient, unsigned Transparency, const unsigned &Margin)
375         {
376             #if DEBUG == true
377             _ksys_debug_puts("KolibriLib::window:::Window constructor\n");
378             #endif
379 
380             _title = Title;
381             _size = size;
382             _MARGIN = Margin;
383             _TitleColor = TitleColor;
384             _Transparency = Transparency;
385             _RealtimeRedraw = RealtimeRedraw;
386 
387             /*DCBAYYYY*/
388             _style = 0b00100000;
389             if (Resize)
390             {
391                 _style += WindowStyle::CanResize;
392             }
393             else
394             {
395                 _style += WindowStyle::FixSize;
396             }
397 
398             if (Gradient)
399             {
400                 _style += WindowStyle::GradientDraw;
401             }
402 
403             if (colors == Colors::DefaultColorTable) // Если небыла в аргументах таблица цветов
404             {                                        // Используется системная
405                 _colors = OS::GetSystemColors();
406             }
407             else
408             {
409                 _colors = colors;
410             }
411 
412             if (_Transparency > 0)
413             {
414                 _style += WindowStyle::NoDrawWorkspace;
415             }
416 
417             window::CreateWindow(DefaultWindowCoord, _size, _title, _colors.work_area, _TitleColor, _style); // Отрисовать окно
418         }
