424         void KolibriLib::window::Window::Redraw()
425         {
426             StartRedraw();
427             ChangeWindow(GetCoord(), _size);
428 
429             if (_Transparency > 0 && false)
430             {
431                 /*UI::Images::img buff;
432                 for (unsigned i = 0; i < GetWindowSize().y; i++)
433                 {
434                     for (unsigned j = 0; j < GetWindowSize().x; j++)
435                     {
436                         buff.SetPixel(Colors::BlendColors(Background::ReadPoint(Coord(j, i)), , _Transparency / 100))
437 
438                     }
439                 }*/
440             }
441             else
442             {
443                 KolibriLib::graphic::DrawRectangleFill({0, (int)window::GetSkinHeight()}, GetWindowSize(), _colors.frame_area);
444             }
445 
446             for (const std::pair<int, Window::Element> &n : Window::_Elements)
447             {
448                 switch (n.second._type)
449                 {
450                 case KolibriLib::window::Window::Element::Type::TextLabel:
451                     n.second.txt->Render();
452                     break;
453                 case KolibriLib::window::Window::Element::Type::Button:
454                     n.second.btn->Render();
455                     break;
456                 case KolibriLib::window::Window::Element::Type::Image:
457                     n.second.img->Render();
458                     break;
459                 case KolibriLib::window::Window::Element::Type::Form:
460                     n.second.form->Render();
461                     break;
462                 case KolibriLib::window::Window::Element::Type::CheckBox:
463                     n.second.checkbox->Render();
464                     break;
465                 case KolibriLib::window::Window::Element::Type::Frame:
466                     n.second.frame->Render();
467                     break;
468                 case KolibriLib::window::Window::Element::Type::Menu:
469                     n.second.menu->Render();
470                     break;
471                 default:
472                     _ksys_debug_puts("KolibriLib::window::Window::Render unknown type, break\n");
473                     break;
474                 }
475             }
476 
477             if (_Transparency != 0) // Прозрачность окна
478             {
479                 for (int i = 0; i < _size.y; i++)
480                 {
481                     for (int j = 0; j < _size.x; j++)
482                     {
483                         graphic::DrawPixel({j, i}, Colors::BlendColors(graphic::ReadPoint({j, i}), Background::ReadPoint({j, i}), 100 / _Transparency)); // Пока так, потом может быть станет лучше
484                     }
485                 }
486             }
487 
488             EndRedraw();
489         }
