532         void Window::Render(const Coord &coord)
533         {
534             StartRedraw();
535             window::CreateWindow(coord, _size, _title, _colors.work_area, _TitleColor, _style);
536 
537             for (const std::pair<int, Element> &n : _Elements)
538             {
539                 switch (n.second._type)
540                 {
541                 case Element::Type::TextLabel:
542                     n.second.txt->Render();
543                     break;
544                 case Element::Type::Button:
545                     n.second.btn->Render();
546                     break;
547                 case Element::Type::Image:
548                     n.second.img->Render();
549                     break;
550                 case Element::Type::Form:
551                     n.second.form->Render();
552                     break;
553                 case Element::Type::CheckBox:
554                     n.second.checkbox->Render();
555                     break;
556                 case Element::Type::Menu:
557                     n.second.menu->Render();
558                     break;
559                 case Element::Type::Frame:
560                     n.second.frame->Render();
561                     break;
562                 default:
563                     break;
564                 }
565             }
566 
567             if (_Transparency != 0) // Прозрачность окна
568             {
569                 Size size = GetWindowSize();
570                 for (int i = 0; i < size.y; i++)
571                 {
572                     for (int j = 0; j < size.x; j++)
573                     {
574                         graphic::DrawPixel({j, i}, Colors::BlendColors(graphic::ReadPoint({j, i}), Background::ReadPoint({j, i}), 100 / _Transparency));
575                     }
576                 }
577             }
578 
579             EndRedraw();
580         }
