32         struct Element
33         {
34             enum class Type
35             {
36                 Button,
37                 Image,
38                 CheckBox,
39                 Form,
40                 TextLabel
41             };
42 
43             UI::buttons::Button btn;
44             UI::text::TextLabel txt;
45             UI::Form frm;
46             UI::CheckBox CheckBox;
47             UI::Images::image img;
48 
49             Type type;
50             bool use;
51 
52             Element(Type t)
53             {
54                 type = t;
55                 use = false;
56             }
57             ~Element()
58             {
59                 switch (type)
60                 {
61                 case Element::Type::Button:
62                     btn.~Button();
63                     break;
64                 case Element::Type::CheckBox:
65                     CheckBox.~CheckBox();
66                 case Element::Type::TextLabel:
67                     txt.~TextLabel();
68                 case Element::Type::Form:
69                     frm.~Form();
70                 case Element::Type::Image:
71                     img.~image();
72                 default:
73                     break;
74                 }
75             }
76         };
