225         unsigned Window::AddNewButton(UI::buttons::Button btn)
226         {
227             for(unsigned i = 0; i < _Elements.size(); i++)
228             {
229                 if (!_Elements[i].use)
230                 {
231                     switch (btn.GetType())
232                     {
233                     case UI::buttons::Content::Type::Image:
234                         _Elements[i].btn.init(btn.GetCoord(), btn.GetSize(), btn.GetImage(), btn.GetMargin(), btn.GetColor());
235                         break;
236                     case UI::buttons::Content::Type::Text:
237                         _Elements[i].btn.init(btn.GetCoord(), btn.GetSize(), btn.GetTextLabel(), btn.GetMargin(), btn.GetColor());
238                         break;
239                     default:
240                         break;
241                     }
242                     _Elements[i].type = Element::Type::Button;
243                     _Elements[i].use = true;
244                     return i;
245                 }
246             }
247             Element a(Element::Type::Button);
248             switch (btn.GetType())
249             {
250             case UI::buttons::Content::Type::Image:
251                 a.btn.init(btn.GetCoord(), btn.GetSize(), btn.GetImage(), btn.GetMargin(), btn.GetColor());
252                 break;
253             case UI::buttons::Content::Type::Text:
254                 a.btn.init(btn.GetCoord(), btn.GetSize(), btn.GetTextLabel(), btn.GetMargin(), btn.GetColor());
255             default:
256                 break;
257             }
258             a.use = true;
259             _Elements.push_back(a);
260             return _Elements.size();
261         }
262 
263         unsigned Window::AddNewTextLabel(UI::text::TextLabel text)
264         {
265             for (unsigned i = 0; i < _Elements.size(); i++)
266             {
267                 if (!_Elements[i].use)
268                 {
269                     _Elements[i].txt = text;
270                     _Elements[i].type = Element::Type::TextLabel;
271                     _Elements[i].use = true;
272                     return i;
273                 }
274             }
275             Element a(Element::Type::TextLabel);
276             a.txt = text;
277             a.use = true;
278             _Elements.push_back(a);
279         }
280 
281         unsigned Window::AddNewForm(UI::Form form)
282         {
283             for (unsigned i = 0; i < _Elements.size(); i++)
284             {
285                 if (!_Elements[i].use)
286                 {
287                     _Elements[i].frm.init(form.GetCoord(), form.GetSize(), NULL, form.GetColor(), form.GetBackgroundColor(), form.GetMargin());
288                     _Elements[i].use = true;
289                     return i;
290                 }
291             }
292             Element a(Element::Type::Form);
293             a.frm.init(form.GetCoord(), form.GetSize(), NULL, form.GetColor(), form.GetBackgroundColor(), form.GetMargin());
294             a.use = true;
295             _Elements.push_back(a);
296             return _Elements.size();
297         }
