299         Window::Window(std::string Title, UI::Size size, int style, Color::ColorsTable colors, unsigned Margin)
300         {
301             _title = Title;
302             _size = size;
303             _style = style;
304             _MARGIN = Margin;
305             
306             if (Color::ComparisonColorsTables(colors, Color::DefaultColorTable))    //Если небыла в аргументах таблица цветов
307             {                                                                       //Используется системная
308                 _colors = OS::GetSystemColors();
309             }
310             else
311             {
312                 _colors = colors;
313             }
314             
315             DrawWindow(DefaultWindowCoord);                                         //Отрисовать окно
316         }
317 
318         Window::~Window()
319         {
320             for(unsigned i = 0; i < _Elements.size(); i++)
321             {
322                 _Elements[i].use = false;
323                 switch (_Elements[i].type)
324                 {
325                 case Element::Type::Button:
326                     _Elements[i].btn.~Button();
327                     break;
328                 case Element::Type::CheckBox:
329                     _Elements[i].CheckBox.~CheckBox();
330                 case Element::Type::TextLabel:
331                     _Elements[i].txt.~TextLabel();
332                 case Element::Type::Form:
333                     _Elements[i].frm.~Form();
334                 case Element::Type::Image:
335                     _Elements[i].img.~image();
336                 default:
337                     break;
338                 }
339             }
340         }
