382         void Window::Render()
383         {
384             StartRedraw();
385             DrawWindow();
386 
387             for (unsigned i = 0; i < _Elements.size(); i++)
388             {
389                 if (_Elements[i].use)
390                 {
391                     switch (_Elements[i].type)
392                     {
393                     case Element::Type::Button:
394                         _Elements[i].btn.Render();
395                         break;
396                     case Element::Type::TextLabel:
397                         _Elements[i].txt.Render();
398                         break;
399                     case Element::Type::CheckBox:
400                         _Elements[i].CheckBox.Render();
401                         break;
402                     case Element::Type::Image:
403                         _Elements[i].img.Render();
404                         break;
405                     default:
406                         break;
407                     }
408                 }
409             }
410 
411             EndRedraw();
412         }
