424         unsigned KolibriLib::window::Window::CreateButton(UI::Coord coord, UI::Size size, std::string Text, unsigned margin, bool UseWindowColors, Color::Color color, Color::Color TextColor)
425         {
426             if(UseWindowColors)
427             {
428                 color       = _colors.work_button;
429                 TextColor   = _colors.work_button_text;
430             }
431             UI::buttons::Button b(coord, size, margin, color);
432             return AddNewButton(b);
433         }
