437         unsigned Window::CreateText(UI::Coord coord, UI::Size size, std::string text, unsigned FontSize, bool UseWindowColors, Color::Color color)
438         {
439             if(UseWindowColors)
440             {
441                 color = _colors.work_text;
442             }
443 
444             UI::text::TextLabel t(coord, size, text, FontSize, true, color, _MARGIN);
445             return AddNewTextLabel(t);
