455         void Window::DeleteElement(unsigned id)
456         {
457             _Elements[id].use = false;
458             switch (_Elements[id].type)
459             {
460             case Element::Type::Button:
461                 _Elements[id].btn.~Button();
462                 break;
463             case Element::Type::CheckBox:
464                 _Elements[id].CheckBox.~CheckBox();
465             case Element::Type::TextLabel:
466                 _Elements[id].txt.~TextLabel();
467             case Element::Type::Form:
468                 _Elements[id].frm.~Form();
469             case Element::Type::Image:
470                 _Elements[id].img.~image();
471             default:
472                 break;
473             }
474         }
475         OS::Event Window::Handler()
476         {
477             OS::Event event = OS::WaitEvent(10);    //ждём ивента 1/10 секунды
478 
479             switch (event)
480             {
481             case OS::Events::Redraw:
482                 Render();
483                 break;
484             case OS::Events::Button:
485                 if(UI::buttons::GetPressedButton() == 1)
486                 {
487                     return OS::Events::Exit;
488                 }
489 
490                 for (unsigned i = 0; i < _Elements.size(); i++)
491                 {
492                     if (_Elements[i].use)
493                     {
494                         if (_Elements[i].type == Element::Type::Form)
495                         {
496                             _Elements[i].frm.ButtonHandler();
497                         }
498                     }
499                 }
500                 break;
501             case OS::Events::Key:
502                 for(unsigned i = 0; i < _Elements.size(); i++)
503                 {
504                     if(_Elements[i].use)
505                     {
506                         if(_Elements[i].type == Element::Type::Form)
507                         {
508                             _Elements[activeForm].frm.Handler();
509                         }
510                     }
511                 }
512                 break;
513             default:
514                 break;
515             }
516             return event;
517         }
